import React from 'react';
import * as module from '../moduleTraitement/generate_synoptique'

export class ArbrePage extends React.Component {
    constructor(props) {
      super(props)
      this.state = {
        myDiagramDiv: "myDiagramDiv"
        };
    //   this.functionAffichageDiagram = this.functionAffichageDiagram.bind(this);
    }
    componentDidMount() {
        // this.functionAffichageDiagram();
        // const baba = module.generateSynoptique(module.nodeDataArray2, module.linkDataArray2, "myDiagramDiv");
        // throw new Error("An error has occured in Buggy component!");
        
        // module.functionToggle("idDisplayArbre", "divGlobalDiagram");
      }
    render() {
        // const myOverviewDivStyle = {
        //     position: 'relative',
        //     WebkitTapHighlightColor: 'rgba(255, 255, 255, 0)',
        //   };
        // const myOverviewDivSecondStyle = {
        //     position: 'absolute',
        //     overflow: 'auto',
        //     width: '71px',
        //     height: '1px',
        //     zIndex: 1
        //   };
        // const myOverviewDivThreeStyle = {
        //     position: 'absolute',
        //     width: '1px',
        //     height: '1px'
        //   };
        const myDiagramDivStyle = {
            backgroundColor: 'white',
            border: '1px solid black',
            width: '100%',
            height: '100vh',
            '--darkreader-inline-bgcolor':'#181a1b',
            '--darkreader-inline-border-top':'#666666',
            '--darkreader-inline-border-right':'#666666',
            '--darkreader-inline-border-bottom':'#666666',
            '--darkreader-inline-border-left':'#666666',
            position: 'relative',
            WebkitTapHighlightColor: 'rgba(255, 255, 255, 0)'
          };
        // const canvasOneStyle = {
        //     position: 'absolute',
        //     top: '0px',
        //     left: '0px',
        //     zIndex: '2',
        //     userSelect: 'none',
        //     width: '71px',
        //     height: '1px'
        // };
        // const canvasSecondStyle = {
        //     position: 'absolute',
        //     top: '0px',
        //     left: '0px',
        //     zIndex: '2',
        //     userSelect: 'none',
        //     width: '69px',
        //     height: '918px'
        // }
        // const myDiagramDivOneStyle = {
        //     position: 'absolute',
        //     verflow: 'auto',
        //     width: '69px',
        //     height: '935px',
        //     zIndex: '1'
        // }
        // const myDiagramDivSecondStyle = {
        //     position: 'absolute',
        //     width: '14396px',
        //     height: '1px'
        // }
        this.functionAffichageDiagram = () => {
            module.generateSynoptique(module.nodeDataArray2, module.linkDataArray2, "myDiagramDiv");
            module.functionToggle("idDisplayArbre", "divGlobalDiagram");
        };//  onClick={this.functionAffichageDiagram} 
      return (
            <div>
                <button id="idDisplayArbre" className="btn btn-outline-info m-2">Cliquer pour afficher l'arbre</button>
                <div id="divGlobalDiagram" className="container-fluid">
                    <input id="mySearch" className="m-2" type="search" placeholder="Chercher par Prenom"></input>
                        <button id="" className="btn btn-primary m-2">Chercher</button>
                    <div id="myDiagramDiv" className="mt-3" style={myDiagramDivStyle} data-darkreader-inline-bgcolor="" data-darkreader-inline-border-top="" data-darkreader-inline-border-right="" data-darkreader-inline-border-bottom="" data-darkreader-inline-border-left="">
                        <h2>{this.state.greeting}</h2>
                    </div>
                </div>
                
            </div>
      );
    }
  }
